package com.te.javahandson.basic;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10;
		int b=20;
		 int res= a++ + ++b;
		 System.out.println(res);
		 
		 int c =10;
		 int d=20;
		 int res_ = c-- + --d;
		  System.out.println(res_);
	}

}
